var fs = require('fs');
var path = require('path');
module.exports = function(dir, extension, callback){
    fs.readdir(dir, function (err, list) {
    if (err){
        return callback(err);
    }
    var ext = '.';
    ext += extension;
    list = list.filter(function (file) {
        return path.extname(file) == ext;
    });
    callback(null, list);
    });
};